sys_ui_action.script:
function onDataSilosAccessAnalysis()
{
	var strTable = GetCurrentRecordTableName();
	var strSysId = GetCurrentRecordSysId();	

	function StateStart()
	{
		if (strSysId == '' || strTable == '')
		{
			DoModalMessage('DataSilos Access Analysis','Error: Could not identify record to analyse.',StateFinish);
			return;
		}
		DoModalPrompt('DataSilos Access Analysis','Please enter a user\'s Sys ID or name:','',StateAccessAnalysis);
	}

	function StateAccessAnalysis(strUser)
	{
		if (strUser == false)
		{
			return;
		}
		var ga = new GlideAjax('DataSilosClientAPI');
		ga.addParam('sysparm_name','AccessAnalysisForUserAndRecord');
		ga.addParam('sysparm_user',strUser);
		ga.addParam('sysparm_table',strTable);
		ga.addParam('sysparm_record',strSysId);
		ga.getXML(StateResponse);
	}

	function StateResponse(response)
	{
		var strResult = response.responseXML.documentElement.getAttribute('answer');
		DoModalMessage('DataSilos Access Analysis',strResult,StateFinish);
	}

	function StateFinish()
	{}

	StateStart();
}
